    /**
     * Shared information in every execution request. This will evolve 
     * over time to support signatures and privacy proofs as the protocol
     * decentralizes
     */
    struct ExecutionRequest {
        //account requesting this execution
        address requester;

        //fees info
        FeeDetails fee;
    }
}

